mapper.select:
		SELECT 
        FROM t_card card_
        WHERE card_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_card
card_type = #{cardType,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, `level` = #{level,jdbcType=INTEGER}, `name` = #{name,jdbcType=VARCHAR}, parent_id = #{parentId,jdbcType=BIGINT}, priority = #{priority,jdbcType=VARCHAR}, project_id = #{projectId,jdbcType=BIGINT}, resolution = #{resolution,jdbcType=VARCHAR}, state = #{state,jdbcType=INTEGER},
WHERE id = #{id,jdbcType=BIGINT}
